import React from "react";

const ProjectContainer = ({ articlename, articleimage }) => {
    return (
        <div className="projects__item">
            <picture className="projects__picture">
                <source media="(min-width: 62.5em)" srcSet={`/assets/images/${articleimage}`} />
                <img className="projects__image" src={`/assets/images/${articleimage}`}
                    alt="screenshot of design portfolio website" width="343" height="253" />
            </picture>
            <h3 className="projects__name">{articlename}</h3>
            <p className="projects__tags">
                {projecttags.map((tag, index) => (
                    <span key={index}>{tag}</span>
                ))}
            </p>
            <div className="projects__links">
                <a href={projecturl} className="underline">View Code</a>
            </div>
        </div>
    );
};

export default ProjectContainer;
