// Article.js
import React, { useState } from "react";
import Articlecontainer from "./article_prop";
import ArticlePage from "./article_page,jsx";

const Article = () => {
    const [selectedArticle, setSelectedArticle] = useState(null);

    const articles = [
        {
            Articlename: "Mamba: Revolutionizing Sequence Modeling Beyond Transformers and RNNs",
            Articleimage: "article1.png"
        }
        // Add more articles here
    ];

    const handleArticleClick = (article) => {
        setSelectedArticle(article);
    };

    return (
        <div className="projects">
            <div className="wrapper projects__wrapper">
                <div className="projects__grid">
                    <h2 className="projects__headline header-xl">Article</h2>
                    <a href="#contact" className="projects__contact underline">Contact me</a>
                    {selectedArticle ? (
                        <ArticlePage
                            title={selectedArticle.Articlename}
                            imageUrl={`/assets/images/${selectedArticle.Articleimage}`}
                            content={
                                <p>Here you can add the article content.</p>
                            }
                        />
                    ) : (
                        articles.map((project, index) => (
                            <Articlecontainer
                                key={index}
                                articlename={project.Articlename}
                                articleimage={project.Articleimage}
                                onArticleClick={handleArticleClick}
                            />
                        ))
                    )}
                </div>
            </div>
        </div>
    );
};

export default Article;
